# Check bank entries\nimport sqlite3\n\n# Connect to database\nconn = sqlite3.connect('society_management.db')\ncursor = conn.cursor()\n\ncursor.execute('''\n    SELECT id, date, description, amount, reconciliation_status\n    FROM bank_statements\n    ORDER BY date ASC\n''')\nentries = cursor.fetchall()\n\nprint('Bank statement entries:')\nfor entry in entries:\n    print(f'  ID: {entry[0]}, Date: {entry[1]}, Amount: {entry[3]}, Status: {entry[4]}, Description: {entry[2][:30]}')\n\n# Check the date range of entries\nif entries:\n    dates = [entry[1] for entry in entries]\n    print(f'\\nDate range: {min(dates)} to {max(dates)}')\n\nconn.close()